import React, { useState } from "react";
import QRCode from "qrcode.react";
import Modal from "react-modal";
import { v4 as uuidv4 } from "uuid"; // Install with `npm install uuid`

const RaffleTicketSystem = () => {
  const [phoneNumber, setPhoneNumber] = useState("");
  const [ticketCount, setTicketCount] = useState(0);
  const [uniqueLink, setUniqueLink] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [userData, setUserData] = useState({ name: "", email: "", agreed: false });

  // Handles QR Code generation
  const handleGenerateQR = () => {
    if (phoneNumber && ticketCount > 0) {
      // Create a unique ID and link
      const uniqueId = uuidv4();
      const link = `http://localhost:3000/tickets/${uniqueId}?tickets=${ticketCount}`;
      setUniqueLink(link);

      // Simulate storing the data on the backend
      console.log("Storing on backend: ", { phoneNumber, ticketCount, uniqueId });
    }
  };

  // Handles ticket selection completion
  const handleTicketSelection = () => {
    setShowModal(true);
  };

  // Handles form submission
  const handleConfirm = () => {
    if (userData.name && userData.email && userData.agreed) {
      console.log("Data sent to backend: ", { ...userData, link: uniqueLink });
      setShowModal(false);
      setPhoneNumber("");
      setTicketCount(0);
      setUniqueLink("");
      setUserData({ name: "", email: "", agreed: false });
    } else {
      alert("Please complete the form and agree to the privacy policy.");
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "500px", margin: "auto" }}>
      <h1>Raffle Ticket System</h1>

      {/* Ticket Counter Form */}
      <div>
        <label>Phone Number: </label>
        <input
          type="text"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          placeholder="Enter phone number"
        />
        <br />
        <label>Number of Tickets: </label>
        <input
          type="number"
          value={ticketCount}
          onChange={(e) => setTicketCount(Number(e.target.value))}
          placeholder="Enter ticket count"
        />
        <br />
        <button onClick={handleGenerateQR}>Generate QR Code</button>
      </div>

      {/* QR Code Display */}
      {uniqueLink && (
        <div style={{ marginTop: "20px" }}>
          <h3>Scan to Select Tickets</h3>
          <QRCode value={uniqueLink} size={200} />
          <p>{uniqueLink}</p>
          <br />
          <button onClick={handleTicketSelection}>Select Tickets</button>
        </div>
      )}

      {/* Modal for Additional Information */}
      <Modal isOpen={showModal} onRequestClose={() => setShowModal(false)}>
        <h2>Complete Your Details</h2>
        <form>
          <label>Name: </label>
          <input
            type="text"
            value={userData.name}
            onChange={(e) => setUserData({ ...userData, name: e.target.value })}
            placeholder="Enter your name"
          />
          <br />
          <label>Email: </label>
          <input
            type="email"
            value={userData.email}
            onChange={(e) => setUserData({ ...userData, email: e.target.value })}
            placeholder="Enter your email"
          />
          <br />
          <label>
            <input
              type="checkbox"
              checked={userData.agreed}
              onChange={(e) => setUserData({ ...userData, agreed: e.target.checked })}
            />
            I agree to the Privacy Policy
          </label>
          <br />
          <button type="button" onClick={handleConfirm}>
            Confirm
          </button>
        </form>
      </Modal>
    </div>
  );
};

export default RaffleTicketSystem;
