import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Reserv'

const ReservedTickets = () => {
  const [reservedTickets, setReservedTickets] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredTickets, setFilteredTickets] = useState([]);

  // Fetch reserved tickets on component mount
  useEffect(() => {
    const fetchReservedTickets = async () => {
      try {
        const response = await axios.get('/api/tickets/reserved');
        setReservedTickets(response.data);
        setFilteredTickets(response.data); // Initialize filtered tickets
      } catch (error) {
        console.error('Error fetching reserved tickets:', error);
      }
    };

    fetchReservedTickets();
  }, []);

  // Handle search bar input
  const handleSearch = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);

    const filtered = reservedTickets.filter((ticket) =>
      ticket.name.toLowerCase().includes(term) ||
      ticket.email.toLowerCase().includes(term) ||
      ticket.phoneNumber.includes(term) ||
      ticket.ticketNumber.toString().includes(term)
    );

    setFilteredTickets(filtered);
  };

  return (
    <div className="reserved-tickets">
      <h1>Reserved Tickets</h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search by name, email, phone, or ticket number..."
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>
      <div className="tickets-list">
        {filteredTickets.length > 0 ? (
          filteredTickets.map((ticket) => (
            <div key={ticket.ticketId} className="ticket-card">
              <p><strong>Ticket Number:</strong> {ticket.ticketNumber}</p>
              <p><strong>Name:</strong> {ticket.name}</p>
              <p><strong>Email:</strong> {ticket.email}</p>
              <p><strong>Phone:</strong> {ticket.phoneNumber}</p>
              <p><strong>Reservation Date:</strong> {new Date(ticket.reservationDate).toLocaleString()}</p>
            </div>
          ))
        ) : (
          <p>No reserved tickets found.</p>
        )}
      </div>
    </div>
  );
};

export default ReservedTickets;
