import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TicketSystem = () => {
  const [tickets, setTickets] = useState([]);
  const [selectedTickets, setSelectedTickets] = useState([]);
  const [formData, setFormData] = useState({ name: '', email: '', phoneNumber: '' });

  useEffect(() => {
    // Fetch available tickets when the component mounts
    axios
      .get('http://localhost:5000/session')  // Fetch all available tickets
      .then((response) => {
        setTickets(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tickets:', error);
      });
  }, []);

  const handleCheckboxChange = (ticketId) => {
    setSelectedTickets((prevSelected) =>
      prevSelected.includes(ticketId)
        ? prevSelected.filter((id) => id !== ticketId) // Unselect ticket if already selected
        : [...prevSelected, ticketId] // Select ticket if not already selected
    );
  };

  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Send reservation request for selected tickets
    axios
      .post('http://localhost:5000/api/tickets/reserve', {
        selectedTickets,
        formData,
      })
      .then((response) => {
        alert('Tickets reserved successfully!');
      })
      .catch((error) => {
        console.error('Error reserving tickets:', error);
      });
  };

  return (
    <div>
      <h1>Available Tickets</h1>
      <form onSubmit={handleSubmit}>
        {tickets.length > 0 ? (
          <div>
            {tickets.map((ticket) => (
              <div key={ticket.ticketId}>
                <input
                  type="checkbox"
                  value={ticket.ticketId}
                  onChange={() => handleCheckboxChange(ticket.ticketId)}
                />
                <label>{ticket.ticketNumber}</label>
              </div>
            ))}
          </div>
        ) : (
          <p>No available tickets.</p>
        )}

        {selectedTickets.length > 0 && (
          <div>
            <h2>Fill in Your Details</h2>
            <input
              type="text"
              name="name"
              placeholder="Your Name"
              value={formData.name}
              onChange={handleFormChange}
            />
            <input
              type="email"
              name="email"
              placeholder="Your Email"
              value={formData.email}
              onChange={handleFormChange}
            />
            <input
              type="text"
              name="phoneNumber"
              placeholder="Your Phone Number"
              value={formData.phoneNumber}
              onChange={handleFormChange}
            />
            <button type="submit">Reserve Tickets</button>
          </div>
        )}
      </form>
    </div>
  );
};

export default TicketSystem;
