import React, { useState, useEffect } from "react";
import { QRCodeCanvas } from "qrcode.react";
import { v4 as uuidv4 } from "uuid";
import axios from "axios"; // For API calls to fetch tickets

const RaffleTicketSystem = () => {
  const [phoneNumber, setPhoneNumber] = useState("");
  const [ticketCount, setTicketCount] = useState(0);
  const [uniqueLink, setUniqueLink] = useState("");
  const [sessionId, setSessionId] = useState(""); // For storing session ID
  const [tickets, setTickets] = useState([]); // To store the available tickets
  const [selectedTickets, setSelectedTickets] = useState([]); // To store selected tickets

  // Fetch available tickets for the session
  useEffect(() => {
    if (sessionId) {
      const fetchTickets = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/tickets/${sessionId}/available`);
          setTickets(response.data); // Set the available tickets
        } catch (error) {
          console.error("Error fetching tickets:", error);
        }
      };
      fetchTickets();
    }
  }, [sessionId]);

  // Handles QR Code generation
  const handleGenerateQR = async () => {
    if (!phoneNumber || ticketCount <= 0) {
      alert("Please enter a valid phone number and ticket count.");
      return;
    }

    const uniqueId = uuidv4(); // Generate a unique session ID
    setSessionId(uniqueId); // Store session ID
    const link = `http://localhost:3000/tickets/${uniqueId}?tickets=${ticketCount}`;
    setUniqueLink(link);

    // Optionally, you can send the user data to the backend to store the session info
    try {
      await axios.post("http://localhost:5000/tickets/create-session", {
        phoneNumber,
        ticketCount,
        sessionId: uniqueId,
      });
      console.log("Session data stored on backend:", { phoneNumber, ticketCount, uniqueId });
    } catch (error) {
      console.error("Error storing session data:", error);
    }
  };

  // Handle ticket selection
  const handleSelectTicket = (ticketNumber) => {
    setSelectedTickets((prevSelected) => {
      if (prevSelected.includes(ticketNumber)) {
        return prevSelected.filter(ticket => ticket !== ticketNumber); // Deselect ticket
      } else {
        return [...prevSelected, ticketNumber]; // Select ticket
      }
    });
  };

  // Handle ticket reservation
  const handleReserveTickets = async () => {
    if (selectedTickets.length === 0) {
      alert("Please select at least one ticket.");
      return;
    }

    try {
      await axios.post(`http://localhost:5000/tickets/${sessionId}/reserve`, {
        ticketNumbers: selectedTickets,
        userDetails: { phoneNumber },
      });
      alert("Tickets reserved successfully!");
      setSelectedTickets([]); // Reset selected tickets after reservation
    } catch (error) {
      console.error("Error reserving tickets:", error);
      alert("Error reserving tickets.");
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Raffle Ticket System</h1>

      <div style={styles.form}>
        <label>Phone Number:</label>
        <input
          type="text"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          placeholder="Enter phone number"
          style={styles.input}
        />
        <label>Number of Tickets:</label>
        <input
          type="number"
          value={ticketCount}
          onChange={(e) => setTicketCount(Number(e.target.value))}
          placeholder="Enter ticket count"
          style={styles.input}
        />
        <button onClick={handleGenerateQR} style={styles.button}>
          Generate QR Code
        </button>
      </div>

      {uniqueLink && (
        <div style={styles.qrSection}>
          <h3>Scan to Confirm Tickets</h3>
          <QRCodeCanvas value={uniqueLink} size={200} />
          <p style={styles.linkText}>{uniqueLink}</p>
        </div>
      )}

      {/* If session ID is available, show available tickets */}
      {sessionId && tickets.length > 0 && (
        <div>
          <h2>Select Your Tickets</h2>
          <div style={styles.ticketGrid}>
            {tickets.map((ticket) => (
              <div
                key={ticket.ticketNumber}
                style={styles.ticketItem}
                onClick={() => handleSelectTicket(ticket.ticketNumber)}
              >
                <button
                  style={{
                    ...styles.button,
                    backgroundColor: selectedTickets.includes(ticket.ticketNumber)
                      ? "green"
                      : "#007BFF",
                  }}
                >
                  Ticket {ticket.ticketNumber}
                </button>
              </div>
            ))}
          </div>
          <button onClick={handleReserveTickets} style={styles.button}>
            Reserve Tickets
          </button>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: { padding: "20px", maxWidth: "500px", margin: "auto" },
  header: { textAlign: "center", marginBottom: "20px" },
  form: { marginBottom: "20px" },
  input: { display: "block", width: "100%", padding: "8px", margin: "10px 0" },
  button: { padding: "10px 20px", background: "#007BFF", color: "#fff", border: "none", cursor: "pointer" },
  qrSection: { marginTop: "20px", textAlign: "center" },
  linkText: { wordBreak: "break-all", color: "#007BFF" },
  ticketGrid: { display: "flex", flexWrap: "wrap", justifyContent: "space-around" },
  ticketItem: { margin: "10px" },
};

export default RaffleTicketSystem;
