import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import 'ticketgrid.css'

// TicketGrid Component
const TicketGrid = () => {
  const { uniqueId } = useParams(); // Extract uniqueId from URL
  const [tickets, setTickets] = useState([]); // State to hold fetched tickets
  const [selectedTickets, setSelectedTickets] = useState([]); // State for selected tickets
  const [ticketCount, setTicketCount] = useState(0); // State to hold the ticket count limit

  // Fetch tickets when component mounts or uniqueId changes
  useEffect(() => {
    axios
      .get("http://localhost:5000/api/tickets") // Fetch available tickets from API
      .then((response) => setTickets(response.data))
      .catch((error) => console.error("Error fetching tickets:", error));
  }, []); // Empty dependency array ensures it runs only once when the component mounts

  // Function to handle ticket selection
  const handleTicketSelect = (ticketId) => {
    // Ensure the number of selected tickets doesn't exceed the limit
    if (selectedTickets.length >= ticketCount) {
      alert(`You can only select up to ${ticketCount} tickets.`);
      return;
    }

    // If ticket is already selected, remove it
    if (selectedTickets.includes(ticketId)) {
      setSelectedTickets(selectedTickets.filter((id) => id !== ticketId));
    } else {
      // Otherwise, add ticket to selected tickets
      setSelectedTickets([...selectedTickets, ticketId]);
    }
  };

  // Function to handle ticket count input change
  const handleTicketCountChange = (e) => {
    const count = parseInt(e.target.value, 10);
    setTicketCount(count); // Update ticket count
  };

  return (
    <div className="ticket-grid-container">
      <h1>Available Tickets</h1>

      <div>
        <label htmlFor="ticketCount">Number of Tickets to Reserve:</label>
        <input
          type="number"
          id="ticketCount"
          value={ticketCount}
          onChange={handleTicketCountChange}
          min="1"
        />
      </div>

      <div className="ticket-grid">
        {tickets.map((ticket) => (
          <div
            key={ticket.id}
            className={`ticket ${selectedTickets.includes(ticket.id) ? "selected" : ""}`}
            onClick={() => handleTicketSelect(ticket.id)}
          >
            <p>Ticket ID: {ticket.ticketNumber}</p>
            <p>Status: {ticket.available ? "Available" : "Reserved"}</p>
          </div>
        ))}
      </div>

      {selectedTickets.length > 0 && (
        <div className="confirmation-modal">
          <p>You have selected {selectedTickets.length} tickets</p>
          <button onClick={() => alert("Redirect to the form")}>Proceed to Form</button>
        </div>
      )}
    </div>
  );
};

export default TicketGrid;
