import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';  // Import useLocation to get query parameters
import axios from 'axios';  // Using axios to make API calls
import './ticketgrid.css';  // Make sure this file is styled accordingly

const TicketGrid = () => {
  const [tickets, setTickets] = useState([]);
  const [selectedTickets, setSelectedTickets] = useState([]);
  const [ticketCount, setTicketCount] = useState(0);  // Dynamically setting ticketCount

  // Use useLocation to get query parameters
  const location = useLocation();

  useEffect(() => {
    // Retrieve ticketCount dynamically from the URL query parameters
    const queryParams = new URLSearchParams(location.search);
    const count = parseInt(queryParams.get('ticketCount'), 10) || 0;  // Default to 0 if not available
    setTicketCount(count);

    // Fetch only available tickets from the API
    axios
      .get('http://localhost:5000/api/tickets')  // Backend endpoint to get available tickets
      .then((response) => {
        setTickets(response.data);  // Store available tickets in state
      })
      .catch((error) => {
        console.error('Error fetching available tickets:', error);
      });
  }, [location.search]);  // Ensure this effect runs whenever the query parameters change

  const handleSelectTicket = (ticketId) => {
    if (selectedTickets.includes(ticketId)) {
      // If the ticket is already selected, unselect it (no alert)
      setSelectedTickets(selectedTickets.filter((id) => id !== ticketId));
    } else if (selectedTickets.length < ticketCount) {
      // If the ticket is not selected and the limit has not been reached, select it
      setSelectedTickets([...selectedTickets, ticketId]);
    }
  };

  const handleReserveTickets = () => {
    if (selectedTickets.length === 0) {
      alert('Please select at least one ticket to reserve.');
      return;
    }

    // Reserve selected tickets
    axios
      .post('http://localhost:5000/api/tickets/reserve', {
        ticketIds: selectedTickets,
        name: 'User Name',  // Replace with real user input
        phoneNumber: '1234567890',
        email: 'user@example.com',
      })
      .then(() => {
        alert('Tickets reserved successfully!');
        setSelectedTickets([]);  // Clear selected tickets after reservation
      })
      .catch((error) => {
        console.error('Error reserving tickets:', error);
      });
  };

  // Function to get the ticket numbers for the selected ticket IDs
  const getSelectedTicketNumbers = () => {
    return selectedTickets
      .map((ticketId) => {
        const ticket = tickets.find((ticket) => ticket.ticketId === ticketId);
        return ticket ? ticket.ticketNumber : null;  // Return ticket number or null if not found
      })
      .filter((ticketNumber) => ticketNumber !== null);  // Filter out null values
  };

  return (
    <div className="ticket-grid">
      <h1>Available Tickets</h1>
      <div className="tickets">
        {tickets.map((ticket) => (
          <div
            key={ticket.ticketId}
            className={`ticket ${selectedTickets.includes(ticket.ticketId) ? 'selected' : ''}`}
            onClick={() => handleSelectTicket(ticket.ticketId)}  // Handle click to select/unselect ticket
          >
            <p>Ticket #{ticket.ticketNumber}</p>
            <p>{ticket.available ? 'Available' : 'Reserved'}</p>
          </div>
        ))}
      </div>

      {/* Actions div - Always visible at the bottom */}
      <div className="actions">
        <p>
          Selected Tickets: {selectedTickets.length}/{ticketCount}
        </p>

        <button onClick={handleReserveTickets} disabled={selectedTickets.length === 0}>
          Reserve Tickets
        </button>
      </div>
    </div>
  );
};

export default TicketGrid;
