import React, { useState } from "react";
import { useParams, useSearchParams } from "react-router-dom";

const TicketConfirmation = () => {
  const { id } = useParams(); // Get unique ticket ID
  const [searchParams] = useSearchParams();
  const ticketCount = searchParams.get("tickets"); // Retrieve ticket count from query params

  const [userData, setUserData] = useState({ name: "", email: "", agreed: false });

  // Form submission handler
  const handleConfirm = () => {
    if (!userData.name || !userData.email || !userData.agreed) {
      alert("Please complete all fields and agree to the privacy policy.");
      return;
    }

    console.log("Data sent to backend:", { id, ticketCount, ...userData });
    alert("Tickets confirmed successfully!");
  };

  return (
    <div style={styles.container}>
      <h1>Confirm Your Tickets</h1>
      <p>Unique ID: {id}</p>
      <p>Tickets to confirm: {ticketCount}</p>

      <form>
        <label>Name:</label>
        <input
          type="text"
          value={userData.name}
          onChange={(e) => setUserData({ ...userData, name: e.target.value })}
          placeholder="Enter your name"
          style={styles.input}
        />
        <label>Email:</label>
        <input
          type="email"
          value={userData.email}
          onChange={(e) => setUserData({ ...userData, email: e.target.value })}
          placeholder="Enter your email"
          style={styles.input}
        />
        <label>
          <input
            type="checkbox"
            checked={userData.agreed}
            onChange={(e) =>
              setUserData({ ...userData, agreed: e.target.checked })
            }
          />
          I agree to the Privacy Policy
        </label>
        <button type="button" onClick={handleConfirm} style={styles.button}>
          Confirm
        </button>
      </form>
    </div>
  );
};

const styles = {
  container: { padding: "20px", maxWidth: "500px", margin: "auto" },
  input: { display: "block", width: "100%", padding: "8px", margin: "10px 0" },
  button: { padding: "10px 20px", background: "#007BFF", color: "#fff", border: "none", cursor: "pointer" },
};

export default TicketConfirmation;
