import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const TicketGrid = () => {
  const { uniqueId } = useParams(); // Extract uniqueId from URL
  const [tickets, setTickets] = useState([]);
  const [selectedTickets, setSelectedTickets] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    phoneNumber: '',
    email: ''
  });

  useEffect(() => {
    // Fetch available tickets based on uniqueId
    axios
      .get(`http://localhost:5000/api/tickets`)
      .then((response) => {
        setTickets(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tickets:', error);
      });
  }, [uniqueId]);

  const handleTicketSelection = (ticketId) => {
    setSelectedTickets((prevSelected) => {
      if (prevSelected.includes(ticketId)) {
        return prevSelected.filter((id) => id !== ticketId); // Deselect
      } else {
        return [...prevSelected, ticketId]; // Select
      }
    });
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleReservation = (e) => {
    e.preventDefault();

    if (selectedTickets.length === 0) {
      alert('Please select at least one ticket.');
      return;
    }

    const data = {
      ticketIds: selectedTickets,
      ...formData
    };

    axios
      .post('http://localhost:5000/api/tickets/reserve', data)
      .then((response) => {
        alert('Tickets reserved successfully!');
      })
      .catch((error) => {
        console.error('Error reserving tickets:', error);
      });
  };

  return (
    <div>
      <h1>Available Tickets</h1>
      <div>
        {tickets.length === 0 ? (
          <p>No available tickets found.</p>
        ) : (
          tickets.map((ticket) => (
            <div key={ticket.ticketId}>
              <label>
                <input
                  type="checkbox"
                  onChange={() => handleTicketSelection(ticket.ticketId)}
                />
                Ticket Number: {ticket.ticketNumber} (Available)
              </label>
            </div>
          ))
        )}
      </div>

      <h2>Reservation Form</h2>
      <form onSubmit={handleReservation}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleFormChange}
            required
          />
        </div>
        <div>
          <label>Phone Number:</label>
          <input
            type="text"
            name="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleFormChange}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleFormChange}
            required
          />
        </div>
        <button type="submit">Reserve Tickets</button>
      </form>
    </div>
  );
};

export default TicketGrid;
