import React, { useEffect, useState } from "react";
import { useParams, useLocation } from "react-router-dom";

const TicketsPage = () => {
  const { uniqueId } = useParams(); // Extract uniqueId from the URL
  const [ticketsAvailable, setTicketsAvailable] = useState(0);
  const location = useLocation();

  useEffect(() => {
    const query = new URLSearchParams(location.search);
    const ticketCount = parseInt(query.get("tickets"), 10);

    // Simulate fetching ticket data from backend
    console.log("Fetching ticket data for ID:", uniqueId);
    setTicketsAvailable(ticketCount);
  }, [uniqueId, location.search]);

  const handleSelectTicket = () => {
    if (ticketsAvailable > 0) {
      setTicketsAvailable(ticketsAvailable - 1);
    } else {
      alert("No more tickets available!");
    }
  };

  return (
    <div>
      <h1>Tickets Available: {ticketsAvailable}</h1>
      <button onClick={handleSelectTicket} disabled={ticketsAvailable <= 0}>
        Select Ticket
      </button>
    </div>
  );
};

export default TicketsPage;
