import React, { useState } from 'react';
import axios from 'axios';
import { QRCodeCanvas } from 'qrcode.react';
import './Ticketsystem.css'; // Import the CSS file

const TicketSystem = () => {
  const [ticketCount, setTicketCount] = useState(0);
  const [uniqueLink, setUniqueLink] = useState('');

  // Handle ticket count input change
  const handleTicketCountChange = (event) => {
    setTicketCount(event.target.value);
  };

  // Generate session link with ticket count
  const generateLink = () => {
    if (ticketCount <= 0) {
      alert('Please enter a valid ticket count.');
      return;
    }

    // Send ticketCount to backend to generate session link
    axios
      .post('http://localhost:5000/api/tickets/generate', { ticketCount })
      .then((response) => {
        setUniqueLink(response.data.url); // URL with the ticketCount query param
      })
      .catch((error) => {
        console.error('Error generating link:', error);
      });
  };

  return (
    <div className="ticket-system-container">
      <h1 className="header">Ticket Reservation System</h1>
      <div className="form-container">
        <label htmlFor="ticketCount" className="label">
          Number of Tickets:
        </label>
        <input
          type="number"
          id="ticketCount"
          value={ticketCount}
          onChange={handleTicketCountChange}
          className="input"
        />
        <button onClick={generateLink} className="generate-button">
          Generate Session Link
        </button>
      </div>

      {uniqueLink && (
        <div className="qr-container">
          <p className="qr-text">Here is your unique session QR Code:</p>
          <QRCodeCanvas value={uniqueLink} size={256} level="H" className="qr-code" />
          <h2 className="link">{uniqueLink}</h2>
        </div>
      )}
    </div>
  );
};

export default TicketSystem;
