import React, { useState } from "react";
import { QRCodeCanvas } from "qrcode.react"; // Updated import for QR code
import Modal from "react-modal"; // Ensure Modal is styled globally
import { v4 as uuidv4 } from "uuid"; // For generating unique IDs

// Set modal styles globally
Modal.setAppElement("#root");

const RaffleTicketSystem = () => {
  const [phoneNumber, setPhoneNumber] = useState("");
  const [ticketCount, setTicketCount] = useState(0);
  const [uniqueLink, setUniqueLink] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [userData, setUserData] = useState({ name: "", email: "", agreed: false });

  // QR Code generation logic
  const handleGenerateQR = () => {
    if (!phoneNumber || ticketCount <= 0) {
      alert("Please enter a valid phone number and ticket count.");
      return;
    }

    const uniqueId = uuidv4();
    const link = `http://localhost:3000/tickets/${uniqueId}?tickets=${ticketCount}`;
    setUniqueLink(link);

    console.log("Storing data on backend:", { phoneNumber, ticketCount, uniqueId });
  };

  // Handle ticket selection completion
  const handleTicketSelection = () => {
    if (!uniqueLink) {
      alert("Generate a QR code first.");
      return;
    }
    setShowModal(true);
  };

  // Form submission handler
  const handleConfirm = () => {
    if (!userData.name || !userData.email || !userData.agreed) {
      alert("Please complete all fields and agree to the privacy policy.");
      return;
    }

    console.log("Data sent to backend:", { ...userData, link: uniqueLink });
    resetForm();
  };

  // Reset form state
  const resetForm = () => {
    setPhoneNumber("");
    setTicketCount(0);
    setUniqueLink("");
    setShowModal(false);
    setUserData({ name: "", email: "", agreed: false });
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Raffle Ticket System</h1>

      {/* Ticket Counter Form */}
      <div style={styles.form}>
        <label>Phone Number:</label>
        <input
          type="text"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          placeholder="Enter phone number"
          style={styles.input}
        />
        <label>Number of Tickets:</label>
        <input
          type="number"
          value={ticketCount}
          onChange={(e) => setTicketCount(Number(e.target.value))}
          placeholder="Enter ticket count"
          style={styles.input}
        />
        <button onClick={handleGenerateQR} style={styles.button}>
          Generate QR Code
        </button>
      </div>

      {/* QR Code Display */}
      {uniqueLink && (
        <div style={styles.qrSection}>
          <h3>Scan to Select Tickets</h3>
          <QRCodeCanvas value={uniqueLink} size={200} />
          <p style={styles.linkText}>{uniqueLink}</p>
          <button onClick={handleTicketSelection} style={styles.button}>
            Select Tickets
          </button>
        </div>
      )}

      {/* Modal for Additional Information */}
      <Modal
        isOpen={showModal}
        onRequestClose={() => setShowModal(false)}
        style={styles.modal}
      >
        <h2>Complete Your Details</h2>
        <form>
          <label>Name:</label>
          <input
            type="text"
            value={userData.name}
            onChange={(e) => setUserData({ ...userData, name: e.target.value })}
            placeholder="Enter your name"
            style={styles.input}
          />
          <label>Email:</label>
          <input
            type="email"
            value={userData.email}
            onChange={(e) => setUserData({ ...userData, email: e.target.value })}
            placeholder="Enter your email"
            style={styles.input}
          />
          <label>
            <input
              type="checkbox"
              checked={userData.agreed}
              onChange={(e) =>
                setUserData({ ...userData, agreed: e.target.checked })
              }
            />
            I agree to the Privacy Policy
          </label>
          <button type="button" onClick={handleConfirm} style={styles.button}>
            Confirm
          </button>
        </form>
      </Modal>
    </div>
  );
};

// Basic styles for the component
const styles = {
  container: { padding: "20px", maxWidth: "500px", margin: "auto" },
  header: { textAlign: "center", marginBottom: "20px" },
  form: { marginBottom: "20px" },
  input: { display: "block", width: "100%", padding: "8px", margin: "10px 0" },
  button: { padding: "10px 20px", background: "#007BFF", color: "#fff", border: "none", cursor: "pointer" },
  qrSection: { marginTop: "20px", textAlign: "center" },
  linkText: { wordBreak: "break-all", color: "#007BFF" },
  modal: {
    content: { top: "50%", left: "50%", right: "auto", bottom: "auto", marginRight: "-50%", transform: "translate(-50%, -50%)" },
  },
};

export default RaffleTicketSystem;
