import React, { useState, useEffect } from "react";
import axios from "axios";

const TicketGrid = () => {
  const [tickets, setTickets] = useState([]);
  const [userDetails, setUserDetails] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phoneNumber: "",
  });
  const [selectedTicket, setSelectedTicket] = useState(null);

  // Fetch available tickets
  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/tickets/available");
        setTickets(response.data);
      } catch (error) {
        console.error("Error fetching tickets:", error);
      }
    };

    fetchTickets(); // Fetch tickets when component mounts
  }, []); // Empty dependency array to run only once

  // Handle ticket selection
  const handleSelectTicket = (ticketNumber) => {
    setSelectedTicket(ticketNumber); // Store the selected ticket number
  };

  // Handle input changes for user details
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  // Handle ticket reservation (send selected ticket and user details to backend)
  const handleReserveTicket = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/tickets/reserve", {
        ticketNumber: selectedTicket,
        userDetails,
      });

      alert(response.data.message); // Server response message
      setSelectedTicket(null); // Reset selected ticket after successful reservation
    } catch (error) {
      console.error("Error reserving ticket:", error);
      alert("Error reserving ticket");
    }
  };

  return (
    <div>
      <h1>Available Tickets</h1>
      <div className="ticket-grid">
        {tickets.map((ticket) => (
          <div key={ticket.ticketNumber} className="ticket-item">
            <button
              onClick={() => handleSelectTicket(ticket.ticketNumber)}
              disabled={!ticket.available} // Disable if the ticket is not available
            >
              {ticket.ticketNumber}
            </button>
          </div>
        ))}
      </div>

      {/* Conditionally render the reservation form if a ticket is selected */}
      {selectedTicket && (
        <div>
          <h2>Reserve Ticket: {selectedTicket}</h2>
          <form onSubmit={(e) => e.preventDefault()}>
            <div>
              <label>First Name:</label>
              <input
                type="text"
                name="firstName"
                value={userDetails.firstName}
                onChange={handleInputChange}
              />
            </div>
            <div>
              <label>Last Name:</label>
              <input
                type="text"
                name="lastName"
                value={userDetails.lastName}
                onChange={handleInputChange}
              />
            </div>
            <div>
              <label>Email:</label>
              <input
                type="email"
                name="email"
                value={userDetails.email}
                onChange={handleInputChange}
              />
            </div>
            <div>
              <label>Phone Number:</label>
              <input
                type="text"
                name="phoneNumber"
                value={userDetails.phoneNumber}
                onChange={handleInputChange}
              />
            </div>
            <button type="button" onClick={handleReserveTicket}>
              Reserve Ticket
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default TicketGrid;
