import React, { useState } from "react";
import { QRCodeCanvas } from "qrcode.react";

const App = () => {
  const [phoneNumber, setPhoneNumber] = useState("");
  const [ticketCount, setTicketCount] = useState(0);
  const [uniqueLink, setUniqueLink] = useState("");

  // Generate QR Code
  const handleGenerateQR = async () => {
    if (!ticketCount || ticketCount <= 0) {
      alert("Please enter a valid ticket count.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/tickets/generate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ticketCount }),
      });

      const data = await response.json();
      if (data.url) {
        setUniqueLink(data.url);
      } else {
        alert("Failed to generate QR code.");
      }
    } catch (error) {
      console.error(error);
      alert("Error generating QR code.");
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>Ticket Reservation System</h1>

      <div style={{ margin: "20px 0" }}>
        <input
          type="text"
          placeholder="Enter phone number"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          style={{ padding: "10px", margin: "5px" }}
        />
        <input
          type="number"
          placeholder="Enter ticket count"
          value={ticketCount}
          onChange={(e) => setTicketCount(Number(e.target.value))}
          style={{ padding: "10px", margin: "5px" }}
        />
        <button onClick={handleGenerateQR} style={{ padding: "10px 20px", margin: "5px" }}>
          Generate QR Code
        </button>
      </div>

      {uniqueLink && (
        <div>
          <h3>Scan to Confirm Tickets</h3>
          <QRCodeCanvas value={uniqueLink} size={200} />
          <p>{uniqueLink}</p>
        </div>
      )}
    </div>
  );
};

export default App;
