import React, { useState } from 'react';
import axios from 'axios';
import { QRCodeCanvas } from 'qrcode.react';  // Import QRCodeCanvas component
import { Link } from 'react-router-dom';

const TicketSystem = () => {
  const [ticketCount, setTicketCount] = useState(0);
  const [uniqueLink, setUniqueLink] = useState('');

  const handleTicketCountChange = (event) => {
    setTicketCount(event.target.value);
  };

  const generateLink = () => {
    if (ticketCount <= 0) {
      alert('Please enter a valid ticket count.');
      return;
    }

    axios
      .post('http://localhost:5000/api/tickets/generate', { ticketCount })
      .then((response) => {
        setUniqueLink(response.data.url);
      })
      .catch((error) => {
        console.error('Error generating link:', error);
      });
  };

  return (
    <div>
      <h1>Ticket Reservation System</h1>
      <div>
        <label htmlFor="ticketCount">Number of Tickets:</label>
        <input
          type="number"
          id="ticketCount"
          value={ticketCount}
          onChange={handleTicketCountChange}
        />
        <button onClick={generateLink}>Generate Session Link</button>
      </div>
      {uniqueLink && (
        <div>
          <p>Here is your unique session QR Code:</p>
          <QRCodeCanvas value={uniqueLink} size={256} level="H" /> {/* Display the QR code */}

        </div>
      )}
    </div>
  );
};

export default TicketSystem;
