import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import './ticketgrid.css'
const TicketGrid = () => {
  const { uniqueId } = useParams(); // Extract uniqueId from URL
  const [tickets, setTickets] = useState([]); // State to hold fetched tickets
  const [selectedTickets, setSelectedTickets] = useState([]); // State for selected tickets
  const [ticketCount, setTicketCount] = useState(0); // State for the maximum ticket count

  // Fetch available tickets and ticket count limit when component mounts
  useEffect(() => {
    // Fetch tickets and ticket count from API or other sources
    axios
      .get("http://localhost:5000/api/tickets") // Adjust to your API for fetching tickets
      .then((response) => setTickets(response.data))
      .catch((error) => console.error("Error fetching tickets:", error));

    // Assuming the ticket count comes from somewhere, like props or a previous page
    setTicketCount(3); // Example of a fixed value, could be dynamic based on user input
  }, []);

  const handleTicketClick = (ticketId) => {
    // Prevent selecting more tickets than the specified ticket count
    if (selectedTickets.length >= ticketCount && !selectedTickets.includes(ticketId)) {
      alert(`You can only select ${ticketCount} tickets.`);
      return;
    }

    // Toggle the selected state of the clicked ticket
    if (selectedTickets.includes(ticketId)) {
      // If the ticket is already selected, unselect it
      setSelectedTickets(selectedTickets.filter((id) => id !== ticketId));
    } else {
      // If the ticket is not selected, select it
      setSelectedTickets([...selectedTickets, ticketId]);
    }
  };

  return (
    <div>
      <h1>Available Tickets</h1>
      <div className="ticket-grid">
      {tickets.map((ticket) => (
          <div
            key={ticket.ticketNumber}
            className={`ticket ${selectedTickets.includes(ticket.Ticket) ? 'selected' : ''}`} // Apply class conditionally
            onClick={() => handleTicketClick(ticket.id)}
          >
            <p>Ticket ID: {ticket.ticketNumber}</p>
          </div>
        ))}
      </div>
      {selectedTickets.length > 0 && (
        <div className="modal">
          <button onClick={() => alert("Proceed to form for confirmation!")}>
            Confirm Selection
          </button>
        </div>
      )}
    </div>
  );
};

export default TicketGrid;
