const handleGenerateQR = () => {
  if (!phoneNumber || ticketCount <= 0) {
    alert("Please enter a valid phone number and ticket count.");
    return;
  }

  const uniqueId = uuidv4(); // Generate unique ID
  const link = `http://localhost:3000/tickets/session/${uniqueId}?tickets=${ticketCount}`;

  // Send data to the backend
  axios
    .post("http://localhost:5000/api/sessions", {
      phoneNumber,
      ticketCount,
      uniqueId,
    })
    .then((response) => {
      setUniqueLink(link); // Update QR link on success
      console.log("Session created successfully:", response.data);
    })
    .catch((error) => {
      console.error("Error creating session:", error);
    });
};
