import React, { useState, useEffect } from 'react';
import { fetchAvailableTickets, reserveTicket } from '../services/ticketService';

const TicketGrid = () => {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [userDetails, setUserDetails] = useState({ firstName: '', lastName: '', email: '', phoneNumber: '' });

  useEffect(() => {
    fetchAvailableTickets().then(setTickets);
  }, []);

  const handleReserve = async () => {
    await reserveTicket(selectedTicket, userDetails);
    setSelectedTicket(null);
    setTickets(await fetchAvailableTickets());
  };

  return (
    <div>
      <h1>Available Tickets</h1>
      {tickets.map((ticket) => (
        <button key={ticket.ticketNumber} onClick={() => setSelectedTicket(ticket.ticketNumber)}>
          {ticket.ticketNumber}
        </button>
      ))}
      {selectedTicket && (
        <form onSubmit={(e) => e.preventDefault()}>
          <input name="firstName" placeholder="First Name" onChange={(e) => setUserDetails({ ...userDetails, firstName: e.target.value })} />
          <button onClick={handleReserve}>Reserve</button>
        </form>
      )}
    </div>
  );
};

export default TicketGrid;
