import React, { useEffect, useState } from 'react';
import { useLocation, useParams } from 'react-router-dom';
import axios from 'axios';
import './ticket'

const TicketGrid = () => {
  const { uniqueId } = useParams();
  const location = useLocation(); // Get the location to access query params
  const [tickets, setTickets] = useState([]);
  const [selectedTickets, setSelectedTickets] = useState([]);
  const ticketCount = new URLSearchParams(location.search).get('ticketCount'); // Extract ticketCount from query param

  useEffect(() => {
    // Fetch available tickets (no need for uniqueId anymore)
    axios
      .get('http://localhost:5000/api/tickets')  // Fetch all available tickets
      .then((response) => {
        setTickets(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tickets:', error);
      });
  }, []);

  const handleTicketSelect = (ticketId) => {
    // Check if the user is already selecting the max number of tickets
    if (selectedTickets.length >= ticketCount) {
      alert(`You can only select up to ${ticketCount} tickets.`);
      return;
    }

    if (selectedTickets.includes(ticketId)) {
      setSelectedTickets(selectedTickets.filter((id) => id !== ticketId));
    } else {
      setSelectedTickets([...selectedTickets, ticketId]);
    }
  };

  return (
    <div>
      <h1>Available Tickets</h1>
      <p>You can select up to {ticketCount} tickets</p>
      <div>
        {tickets.length === 0 ? (
          <p>No available tickets found.</p>
        ) : (
          tickets.map((ticket) => (
            <div key={ticket.ticketId}>
              <label>
                <input
                  type="checkbox"
                  disabled={selectedTickets.length >= ticketCount && !selectedTickets.includes(ticket.ticketId)} // Disable if limit reached
                  onChange={() => handleTicketSelect(ticket.ticketId)}
                />
                Ticket Number: {ticket.ticketNumber} (Available)
              </label>
            </div>
          ))
        )}
      </div>
      <div>
        <h3>Selected Tickets:</h3>
        {selectedTickets.map((ticketId) => (
          <div key={ticketId}>Ticket ID: {ticketId}</div>
        ))}
      </div>
    </div>
  );
};

export default TicketGrid;
