import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import './ticket'

const TicketGrid = () => {
  const { uniqueId } = useParams(); // Extract uniqueId from URL
  const [tickets, setTickets] = useState([]); // State to hold fetched tickets
  const [selectedTickets, setSelectedTickets] = useState([]); // State for selected tickets
  const [ticketCount, setTicketCount] = useState(0); // State for the maximum ticket count

  // Fetch available tickets and ticket count limit when component mounts
  useEffect(() => {
    // Fetch tickets and ticket count from API or other sources
    axios
      .get("http://localhost:5000/api/tickets") // Adjust to your API for fetching tickets
      .then((response) => setTickets(response.data))
      .catch((error) => console.error("Error fetching tickets:", error));

    // Fetch ticket count limit from the server or via URL (modify accordingly)
    const countFromUrl = new URLSearchParams(window.location.search).get("ticketCount");
    setTicketCount(parseInt(countFromUrl, 10)); // Set the max ticket count
  }, [uniqueId]); // Re-fetch tickets if uniqueId changes

  // Function to handle ticket selection
  const handleTicketSelect = (ticketId) => {
    if (selectedTickets.length >= ticketCount) {
      alert(`You can only select ${ticketCount} ticket(s).`);
      return;
    }

    // Add or remove selected ticket based on current state
    if (selectedTickets.includes(ticketId)) {
      setSelectedTickets(selectedTickets.filter((id) => id !== ticketId));
    } else {
      setSelectedTickets([...selectedTickets, ticketId]);
    }
  };

  return (
    <div className="ticket-grid-container">
      <h1>Available Tickets</h1>

      <div className="ticket-grid">
        {tickets.map((ticket) => (
          <div
            key={ticket.id}
            className={`ticket ${selectedTickets.includes(ticket.id) ? "selected" : ""}`}
            onClick={() => handleTicketSelect(ticket.id)} // Toggle selection on click
          >
            <p>Ticket ID: {ticket.ticketNumber}</p>
            <p>Status: {ticket.available ? "Available" : "Reserved"}</p>
          </div>
        ))}
      </div>

      {selectedTickets.length > 0 && (
        <div className="confirmation-modal">
          <p>You have selected {selectedTickets.length} ticket(s)</p>
          <button onClick={() => alert("Redirect to the form")}>Proceed to Form</button>
        </div>
      )}
    </div>
  );
};

export default TicketGrid;
