import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import './ticketgrid.css'

const TicketGrid = () => {
  const location = useLocation(); // Get the location to access query params
  const [tickets, setTickets] = useState([]);
  const [selectedTickets, setSelectedTickets] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const ticketCount = new URLSearchParams(location.search).get("ticketCount");

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/tickets")  // Fetch all available tickets
      .then((response) => setTickets(response.data))
      .catch((error) => console.error("Error fetching tickets:", error));
  }, []);

  const handleTicketSelect = (ticketId) => {
    if (selectedTickets.length >= ticketCount) {
      alert(`You can only select up to ${ticketCount} tickets.`);
      return;
    }

    if (selectedTickets.includes(ticketId)) {
      setSelectedTickets(selectedTickets.filter((id) => id !== ticketId));
    } else {
      setSelectedTickets([...selectedTickets, ticketId]);
    }
  };

  const handleConfirmSelection = () => {
    // You can navigate to the form or perform any action here
    // For example, redirecting to a form page
    window.location.href = "/form"; // Update to your desired URL
  };

  return (
    <div>
      <h1>Available Tickets</h1>
      <p>You can select up to {ticketCount} tickets</p>

      <div className="ticket-grid">
        {tickets.length === 0 ? (
          <p>No available tickets found.</p>
        ) : (
          tickets.map((ticket) => (
            <div
              key={ticket.ticketId}
              className={`ticket-card ${selectedTickets.includes(ticket.ticketId) ? "selected" : ""}`}
              onClick={() => handleTicketSelect(ticket.ticketId)}
            >
              <div className="ticket-info">
                <p>Ticket Number: {ticket.ticketNumber}</p>
                <p>Status: {ticket.status}</p>
              </div>
            </div>
          ))
        )}
      </div>

      <div
        className={`modal-overlay ${selectedTickets.length > 0 ? "show" : ""}`}
      >
        <div className="modal-content">
          <h3>{selectedTickets.length} ticket(s) selected</h3>
          <button onClick={handleConfirmSelection}>Confirm Selection</button>
        </div>
      </div>
    </div>
  );
};

export default TicketGrid;
