import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ReserveTicketForm from '../components/ReserveTicketForm';
import './ticketgrid'

const TicketSystem = () => {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);

  useEffect(() => {
    // Fetch available tickets from the backend
    axios
      .get('http://localhost:5000/api/tickets')
      .then((response) => setTickets(response.data))
      .catch((error) => console.error('Error fetching tickets:', error));
  }, []);

  const handleReserveClick = (ticket) => {
    setSelectedTicket(ticket); // Show reservation form for the selected ticket
  };

  const handleReservationSuccess = (ticketId) => {
    // Update ticket state to reflect reservation
    setTickets(tickets.map(ticket => ticket.ticketId === ticketId ? { ...ticket, available: false } : ticket));
    setSelectedTicket(null); // Close the reservation form
  };

  return (
    <div>
      <h1>Ticket Reservation System</h1>
      <div className="ticket-grid">
        {tickets.map((ticket) => (
          <div key={ticket.ticketId} className="ticket-card">
            <p>Ticket #{ticket.ticketNumber}</p>
            {ticket.available ? (
              <button onClick={() => handleReserveClick(ticket)}>Reserve</button>
            ) : (
              <p>Reserved</p>
            )}
          </div>
        ))}
      </div>

      {selectedTicket && (
        <ReserveTicketForm
          ticket={selectedTicket}
          onReservationSuccess={handleReservationSuccess}
        />
      )}
    </div>
  );
};

export default TicketSystem;
